import React from 'react';
import ReactDOM from 'react-dom';

let greeting;
let current = new Date();
let currenttime=current.getHours();
// let greetingColor={}
if(currenttime>=1 && currenttime<=12 ){
  greeting = 'morning';
  // greetingColor = { backgroundColor: "green"}
}
if (currenttime>12 && currenttime<=19) {
  greeting ="Evening";

} else {
  greeting = "Night";
}


ReactDOM.render(
  <h1>`Hello sir,Good ${greeting}`</h1>,
  document.getElementById('root')
);
