import React, { useState } from 'react';

const App = () =>{
    const [name, setName]= useState("sir");
    const type = (event) =>{
        setName(event.target.value);
    }
    return(
        <>
            <div>
                <h1>Hello {name}</h1>
                <input type="text" 
                 placeholder="Enter your name"
                 onChange={type}
                 value={name}></input>
                <button>Click me</button>
            </div>
        </>
    )
};

export default App;
